// src/components/Transcription.js
import React, { useState } from 'react';
import { w3cwebsocket as W3CWebSocket } from 'websocket';
import Microphone from './Microphone';


const Transcription = () => {
  const [transcript, setTranscript] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleAudioStream = (audioBlob) => {
    const apiKey = 'YOUR_DEEPGRAM_API_KEY';
    const socket = new W3CWebSocket(
      `wss://api.deepgram.com/v1/listen?access_token=${apiKey}`
    );

    socket.onopen = () => {
      const reader = new FileReader();
      reader.readAsArrayBuffer(audioBlob);
      reader.onloadend = () => {
        const audioArrayBuffer = reader.result;
        socket.send(audioArrayBuffer);
      };
    };

    socket.onmessage = (message) => {
      const data = JSON.parse(message.data);
      if (data && data.channel && data.channel.alternatives[0].transcript) {
        setTranscript((prevTranscript) => prevTranscript + ' ' + data.channel.alternatives[0].transcript);
      }
    };

    socket.onerror = (error) => {
      setErrorMessage('Error with transcription service.');
      console.error('WebSocket error:', error);
    };
  };

  return (
    <div>
      <h2>Live Transcription</h2>
      <Microphone handleAudioStream={handleAudioStream} />
      {errorMessage && <p className="error">{errorMessage}</p>}
      <div className="transcript-box">
        <p>{transcript}</p>
      </div>
    </div>
  );
};

export default Transcription;
